import React from "react";
import ReactDOM from "react-dom";


function validate(name, email, password) {
    // we are going to store errors for all fields
    // in a signle array
    const errors = {
        name: '',
        email: '',
        password: '',
    };

    if (name.length === 0) {
        errors[name]="Name can't be empty";
    }

    if (email.length < 5) {
        errors[email]="Email should be at least 5 charcters long"
    }

    if (password.length < 6) {
        errors[password]="Password should be at least 6 characters long"
    }

    return errors;
}

export default class Test extends React.Component {
    constructor() {
        super();
        this.state = {
            errors: {
                name: '',
                email: '',
                password: '',
            }
        };

        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {
        e.preventDefault();

        const name = ReactDOM.findDOMNode(this._nameInput).value;
        const email = ReactDOM.findDOMNode(this._emailInput).value;
        const password = ReactDOM.findDOMNode(this._passwordInput).value;

        const errors = validate(name, email, password);
        if (errors.length > 0) {
            this.setState({ errors });
            return;
        }

        // submit the data...
    }

    render() {
        const { errors } = this.state;
        return (
            <form onSubmit={this.handleSubmit}>
                <input
                    ref={nameInput => (this._nameInput = nameInput)}
                    type="text"
                    placeholder="Name"
                />
                {
                    <p>Error: {errors.name}</p>
                }
                <input
                    ref={emailInput => (this._emailInput = emailInput)}
                    type="text"
                    placeholder="Email"
                />
                {
                    <p>Error: {errors.email}</p>
                }
                <input
                    ref={passwordInput => (this._passwordInput = passwordInput)}
                    type="password"
                    placeholder="Password"
                />
                {
                    <p>Error: {errors.password}</p>
                }
                <button type="submit">Submit</button>
            </form>
        );
    }
}
