import React, { useState } from "react";
import ReactDOM from "react-dom";

export default function CheckboxGroup(props) {

    const initialValues = {
        price: [],
    };

    const [formValues, setFormValues] = useState(initialValues);

    // const [checkedItems, setCheckedItems] = useState({});

    const handleChange = (event) => {
        setFormValues({...formValues, [event.target.name]: event.target.checked});

        console.log(formValues.price)
    }

    // const handleChange = event => {
    //     setCheckedItems({
    //         ...checkedItems,
    //         [event.target.name]: event.target.checked
    //     });
    //     {console.log(checkedItems)}
    // };

    return (
        <div>
            {props.checkboxes.map(item => (
                <div className="custom-control margin-bottom-5 custom-checkbox mr-sm-2">
                    <input
                        type={"checkbox"}
                        name={item.name}
                        checked={formValues.price[item.name]}
                        onChange={handleChange}
                        className="custom-control-input"
                    />
                    <label key={item.key} className="custom-control-label">
                        {item.name}
                    </label>
                </div>
            ))}

        </div>
    );
};
