import React from "react";

const Users = [
    {
        id: 1,
        selected: false,
        name: "Анна Коноваленко",
        resume: "Resume_1.pdf",
        contact: "sincere@april.biz",
        date: "28.10"
    },
    {
        id: 2,
        selected: false,
        name: "Анна Коноваленко",
        resume: "Resume_1.pdf",
        contact: "sincere@april.biz",
        date: "28.10"
    },
    {
        id: 3,
        selected: false,
        name: "Анна Коноваленко",
        resume: "Resume_1.pdf",
        contact: "sincere@april.biz",
        date: "28.10"
    },
    {
        id: 5,
        selected: false,
        name: "Анна Коноваленко",
        resume: "Resume_1.pdf",
        contact: "sincere@april.biz",
        date: "28.10"
    },
];

class Test extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            List: Users,
            MasterChecked: false,
            SelectedList: [],
        };
    }

    // Select/ UnSelect Table rows
    onMasterCheck(e) {
        let tempList = this.state.List;
        // Check/ UnCheck All Items
        tempList.map((user) => (user.selected = e.target.checked));

        //Update State
        this.setState({
            MasterChecked: e.target.checked,
            List: tempList,
            SelectedList: this.state.List.filter((e) => e.selected),
        });
    }

    // Update List Item's state and Master Checkbox State
    onItemCheck(e, item) {
        let tempList = this.state.List;
        tempList.map((user) => {
            if (user.id === item.id) {
                user.selected = e.target.checked;
            }
            return user;
        });

        //To Control Master Checkbox State
        const totalItems = this.state.List.length;
        const totalCheckedItems = tempList.filter((e) => e.selected).length;

        // Update State
        this.setState({
            MasterChecked: totalItems === totalCheckedItems,
            List: tempList,
            SelectedList: this.state.List.filter((e) => e.selected),
        });
    }

    // Event to get selected rows(Optional)
    getSelectedRows() {
        this.setState({
            SelectedList: this.state.List.filter((e) => e.selected),
        });
    }

    render() {
        return (
                    <div className="table-div-container">
                        <table className="table-container">
                            <thead>
                            <tr>
                                <th scope="col" className="title-table-text">
                                    <input
                                        type="checkbox"
                                        className="form-check-input"
                                        checked={this.state.MasterChecked}
                                        id="mastercheck"
                                        onChange={(e) => this.onMasterCheck(e)}
                                    />
                                </th>
                                <th scope="col" className="title-table-text">Прізвище та ім'я</th>
                                <th scope="col" className="title-table-text">Резюме</th>
                                <th scope="col" className="title-table-text">Контакти</th>
                                <th scope="col" className="title-table-text">Дата подачі</th>
                            </tr>
                            </thead>
                            <tbody>
                            {this.state.List.map((user) => (
                                <tr key={user.id} className={user.selected ? "selected" : ""}>
                                    <th scope="row">
                                        <input
                                            type="checkbox"
                                            checked={user.selected}
                                            className="form-check-input"
                                            id="rowcheck{user.id}"
                                            onChange={(e) => this.onItemCheck(e, user)}
                                        />
                                    </th>
                                    <td className="td-table-text">{user.name}</td>
                                    <td className="td-table-text">
                                        <button className="resume-table-button">
                                            <Download size={18} color={"var(--black"} className={"download-employer-applies"}/>
                                            {user.resume}
                                        </button>
                                    </td>
                                    <td className="td-table-text">{user.contact}</td>
                                    <td className="td-table-text">{user.date}</td>
                                </tr>
                            ))}
                            </tbody>
                        </table>

                        <button
                            className="btn btn-primary"
                            onClick={() => this.getSelectedRows()}
                        >
                            Get Selected Items {this.state.SelectedList.length}
                        </button>
                        <div className="row">
                            <b>All Row Items:</b>
                            <code>{JSON.stringify(this.state.List)}</code>
                        </div>
                        <div className="row">
                            <b>Selected Row Items(Click Button To Get):</b>
                            <code>{JSON.stringify(this.state.SelectedList)}</code>
                        </div>
                    </div>
        );
    }
}

export default Test;
