import React, {Component, useState} from "react";
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListItemText from '@mui/material/ListItemText';
import Select from '@mui/material/Select';
import SelectCategories from '../vacancy/SelectCategories';
import Checkbox from '@mui/material/Checkbox';
import {Sliders, ChevronDown} from "react-feather";
import SelectForm from "../helpers/selectForm";
import CheckboxGroup from "../helpers/checkboxGroup";
import { useMediaQuery } from 'react-responsive'

export default function Filters(props) {

    const isDesktop = useMediaQuery({
        query: '(min-width: 992px)'
    })

    // const filterVacancies = () => {
    //     alert(`User Created!
    //     Name: ${inputs.schedule}`
    //     )
    // }
    
    // const {inputs, handleInputChange, handleSubmit} =
    //     useForm({city: {[""]}, location: {[""]}, category: {[""]}, schedule: {[""]}, filterVacancies);
    
    // const [city, setCity] = useState(null)
    // const [location, setLocation] = useState(null)
    // const [category, setCategory] = useState(null)
    // const [schedule, setSchedule] = useState(null)

    const initialValues = {
        city: '',
        remote: '',
        category: '',
        schedule: '',
        type: '',
    };

    const [formValues, setFormValues] = useState(initialValues);

    const handleSubmit = (e) => {
        e.preventDefault();
        // setFormErrors(validate(formValues));
        // setIsSubmit(true);
    }


    const cities = [
        {value: 'Київ', label: "Київ"},
        {value: 'Одеса', label: "Одеса"},
        {value: 'Львів', label: "Львів"},
    ]

    // const schedules = [
    //     {id: 1, name:"Full-time"},
    //     {id: 2, name:"Part-time"},
    //     {id: 3, name:"Гнучкий full-time"},
    // ]

    const schedules = [
        "Full-time",
        "Part-time",
       "Гнучкий full-time",
    ]

    const locations = [
        "Тільки віддалено"
    ]

    const opportunities = [
        "Стажування",
        "Junior-вакансії",
    ]

    const prices = [
        "Безкоштовно",
        "Платно",
    ]

    // function handleInputChange(event) {
    //     const target = event.target;
    //     const value = target.type === 'checkbox' ? target.checked : target.value;
    //     const name = target.name;
    //
    //     setSchedule({
    //         [name]: value
    //     });
    //
    //     console.log(schedule)
    // }

    return (
        <form className="filter-padding" onSubmit={handleSubmit}>
            {
                isDesktop &&
                <p className="filters-title-text">
                    <Sliders size={18} color={"var(--gray)"} className={"filter-icon-margin"}/>
                    Фільтри
                </p>
            }
            <SelectForm
                name={"city"}
                multiple={false}
                title={"Місто"}
                placeholder={"Оберіть місто"}
                options={cities}
                // value={city}
                // onChange={handleInputChange}
            />
            <CheckboxGroup
                name={"location"}
                title={null}
                options={locations}
                // checked={location}
                // onChange={handleInputChange}
            />

            <SelectCategories isUsedInFilter={true}/>

            {
                props.opportunitites ? (
                    <CheckboxGroup
                        name={"price"}
                        title={"Вартість"}
                        options={prices}
                        // checked={inputs.schedule}
                        // onChange={handleInputChange}
                    />
                ) : (
                    <React.Fragment>
                        <CheckboxGroup
                            name={"schedule"}
                            title={"Графік"}
                            options={schedules}
                            // checked={inputs.schedule}
                            // onChange={handleInputChange}
                        />
                        <CheckboxGroup
                        name={"opportunity"}
                        title={"Вид можливості"}
                        options={opportunities}
                        // checked={inputs.schedule}
                        // onChange={handleInputChange}
                        />
                    </React.Fragment>
                )

            }

            <buttton className="round-button-border round-button-filter">
                Показати
            </buttton>
        </form>
    );
}
