import React from 'react';
import {Layers, TrendingUp} from "react-feather"
import { Link } from 'react-router-dom';

export default function Switch() {

    if(window.location.href==="/all-vacancies"){
        let button = document.getElementById("button-vacancies");
        button.setAttribute('class', "active-switch");
        let button1 = document.getElementById("button-opp");
        button1.setAttribute('class', "not-active-switch");
    }
    else if (window.location.href==="/all-opportunities"){
        let button = document.getElementById("button-opp");
        button.setAttribute('class', "active-switch");
        let button1 = document.getElementById("button-vacancies");
        button1.setAttribute('class', "not-active-switch");
    }

    return (
        <div className="gray-switch-background display-center">
            <div className="padding-switch">
                <Link to="/all-vacancies" id="button-vacancies"  className="text-switch">
                    <Layers size={20} color={"var(--orange)"} className={"layers-icon"}/>
                    Junior-вакансії та стажування
                </Link>
            </div>
            <div className="not-active-switch">
                <Link to="/all-opportunities" id="button-opp" className="text-switch">
                    <TrendingUp size={20} color={"var(--black)"} className={"trending-icon"}/>
                    Освітні можливості
                </Link>
            </div>
        </div>
    )
}
