import React, {Component, useState} from "react";
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListItemText from '@mui/material/ListItemText';
import Select from '@mui/material/Select';
import Checkbox from '@mui/material/Checkbox';
import {Sliders, ChevronDown} from "react-feather";
import SelectForm from "../selectForm";
import CheckboxGroup from "../checkboxGroup";
import { useMediaQuery } from 'react-responsive'

export default function Filters(props) {

    const isDesktop = useMediaQuery({
        query: '(min-width: 992px)'
    })

    // const filterVacancies = () => {
    //     alert(`User Created!
    //     Name: ${inputs.schedule}`
    //     )
    // }
    
    // const {inputs, handleInputChange, handleSubmit} =
    //     useForm({city: {[""]}, location: {[""]}, category: {[""]}, schedule: {[""]}, filterVacancies);
    
    // const [city, setCity] = useState(null)
    // const [location, setLocation] = useState(null)
    // const [category, setCategory] = useState(null)
    // const [schedule, setSchedule] = useState(null)

    const cities = [
        {value: 'Київ', label: "Київ"},
        {value: 'Одеса', label: "Одеса"},
        {value: 'Львів', label: "Львів"},
    ]

    const categories = [
        {value: 'IT-розробка', label: "IT-розробка"},
        {value: 'Аналітика', label: "Аналітика"},
        {value: 'Дизайн', label: "Дизайн"},
        {value: 'Маркетинг', label: "Маркетинг"},
        {value: 'Фінанси', label: "Фінанси"},
        {value: 'Аудит', label: "Аудит"},
        {value: 'Право', label: "Право"},
        {value: 'Логістика', label: "Логістика"},
    ];

    // const schedules = [
    //     {id: 1, name:"Full-time"},
    //     {id: 2, name:"Part-time"},
    //     {id: 3, name:"Гнучкий full-time"},
    // ]

    const schedules = [
        "Full-time",
        "Part-time",
       "Гнучкий full-time",
    ]

    const locations = [
        "Тільки віддалено"
    ]

    const opportunities = [
        "Стажування",
        "Junior-вакансії",
    ]

    const prices = [
        "Безкоштовно",
        "Платно",
    ]

    // function handleInputChange(event) {
    //     const target = event.target;
    //     const value = target.type === 'checkbox' ? target.checked : target.value;
    //     const name = target.name;
    //
    //     setSchedule({
    //         [name]: value
    //     });
    //
    //     console.log(schedule)
    // }

    return (
        <form className="filter-padding">
            {
                isDesktop &&
                <p className="filters-title-text">
                    <Sliders size={18} color={"var(--gray)"} className={"filter-icon-margin"}/>
                    Фільтри
                </p>
            }
            <SelectForm
                name={"city"}
                multiple={false}
                title={"Місто"}
                placeholder={"Оберіть місто"}
                options={cities}
                // value={city}
                // onChange={handleInputChange}
            />
            <CheckboxGroup
                name={"location"}
                title={null}
                options={locations}
                // checked={location}
                // onChange={handleInputChange}
            />
            <SelectForm
                name={"category"}
                multiple={true}
                title={"Категорія"}
                placeholder={"Оберіть категорію"}
                options={categories}
                // value={category}
                // onChange={handleInputChange}
            />

            {
                props.opportunitites ? (
                    <CheckboxGroup
                        name={"price"}
                        title={"Вартість"}
                        options={prices}
                        // checked={inputs.schedule}
                        // onChange={handleInputChange}
                    />
                ) : (
                    <React.Fragment>
                        <CheckboxGroup
                            name={"schedule"}
                            title={"Графік"}
                            options={schedules}
                            // checked={inputs.schedule}
                            // onChange={handleInputChange}
                        />
                        <CheckboxGroup
                        name={"opportunity"}
                        title={"Вид можливості"}
                        options={opportunities}
                        // checked={inputs.schedule}
                        // onChange={handleInputChange}
                        />
                    </React.Fragment>
                )

            }

            <buttton className="round-button-border round-button-filter">
                Показати
            </buttton>

            {/*<React.Fragment>*/}
            {/*    <label className="label-form">*/}
            {/*        /!*{props.title}*!/*/}
            {/*    </label>*/}
            {/*    {schedules.map(function(item, i){*/}
            {/*        return (*/}
            {/*            <div className="custom-control margin-bottom-5 custom-checkbox mr-sm-2">*/}
            {/*                <input*/}
            {/*                    type="checkbox"*/}
            {/*                    className="custom-control-input"*/}
            {/*                    id={i}*/}
            {/*                    name={item.name}*/}
            {/*                    checked={inputs[item.name]}*/}
            {/*                    onChange={handleInputChange}*/}
            {/*                />*/}
            {/*                <label*/}
            {/*                    className="custom-control-label"*/}
            {/*                    htmlFor={i}*/}
            {/*                >*/}
            {/*                    {i}*/}
            {/*                </label>*/}
            {/*            </div>*/}
            {/*        )*/}
            {/*    })}*/}

            {/*</React.Fragment>*/}
        </form>
    );
}
