// import PDF from "../../../../images/pdf.svg";
// import DOC from "../../../../images/doc.svg";
// import PNG from "../../../../images/png.svg";


const getImgByExtension = (fileName) => {
    if (!fileName || fileName === null || typeof fileName === "undefined")
        return null;

    const extension = String(fileName)
        .split('.') // Divide the string into '.'-bordered particles
        .pop();     // Extract last element after '.': file-name.extension -> extension extraction.

    switch (extension) {
        case "pdf":
            return PDF;

        case "doc":
            return DOC;

        case "png":
            return PNG;

        case "jpg":
            return PNG;
    }
};


export {
    getImgByExtension
};
