import React, {useState} from 'react';
import {ChevronLeft, Download} from "react-feather"
import EmployerSwitch from "../components/employerSwitch"
import deloitte from "../images/deloitte.png"
import procredit from "../images/procredit.png"
import fozzy from "../images/img.png"
import EmployerVacancyCard from "../components/employerVacancyCard"
import { useTable, useRowSelect } from 'react-table'

export default function EmployerApplies() {

    const [state, setState] = useState({
        employerName: null,
        employerLogo: null,
        employerDescription: null,
        position: null,
        category: null,
        salary: null,
        location: null,
        vacancyDescription: null,
    })

    // const data = [
    //     {
    //         id: 1,
    //         title: "Коваленко Анна",
    //         description: "Resume_1.pdf",
    //         company: "28.10",
    //     },
    // ]

    const data = React.useMemo(
        () => [
            {
                col1: 'Hello',
                col2: 'World',
                col3: "Коваленко Анна",
                col4: "28.10",
            },
            {
                col1: 'Hello',
                col2: 'World',
                col3: "Коваленко Анна",
                col4: "28.10",
            },
            {
                col1: 'Hello',
                col2: 'World',
                col3: "Коваленко Анна",
                col4: "28.10",
            },
        ],
        []
    )

    const columns = React.useMemo(
        () => [
            {
                Header: 'Прізвище та ім`я',
                accessor: 'name',
            },
            {
                Header: 'Резюме',
                accessor: 'resume',
            },
            {
                Header: 'Контакти',
                accessor: 'contacts',
            },
            {
                Header: 'Дата подачі',
                accessor: 'data',
            },
        ],
        []
    )

    const IndeterminateCheckbox = React.forwardRef(
        ({ indeterminate, ...rest }, ref) => {
            const defaultRef = React.useRef()
            const resolvedRef = ref || defaultRef

            React.useEffect(() => {
                resolvedRef.current.indeterminate = indeterminate
            }, [resolvedRef, indeterminate])

            return (
                <>
                    <input type="checkbox" ref={resolvedRef} {...rest} />
                </>
            )
        }
    )

    function Table({ columns, data }) {
        // Use the state and functions returned from useTable to build your UI
        const {
            getTableProps,
            getTableBodyProps,
            headerGroups,
            rows,
            prepareRow,
            selectedFlatRows,
            state: { selectedRowIds },
        } = useTable(
            {
                columns,
                data,
            },
            useRowSelect,
            hooks => {
                hooks.visibleColumns.push(columns => [
                    // Let's make a column for selection
                    {
                        id: 'selection',
                        // The header can use the table's getToggleAllRowsSelectedProps method
                        // to render a checkbox
                        Header: ({ getToggleAllRowsSelectedProps }) => (
                            <div>
                                <IndeterminateCheckbox {...getToggleAllRowsSelectedProps()} />
                            </div>
                        ),
                        // The cell can use the individual row's getToggleRowSelectedProps method
                        // to the render a checkbox
                        Cell: ({ row }) => (
                            <div>
                                <IndeterminateCheckbox {...row.getToggleRowSelectedProps()} />
                            </div>
                        ),
                    },
                    ...columns,
                ])
            }
        )
        return (
            <div className="table-div-container">

                <table className="table-container" {...getTableProps()}>
                    <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th className="title-table-text" {...column.getHeaderProps()}>{column.render('Header')}</th>
                            ))}
                        </tr>
                    ))}
                    </thead>

                    <tbody {...getTableBodyProps()}>
                    {/*{rows.map(row => {*/}
                    {/*    prepareRow(row)*/}
                    {/*    return (*/}
                    {/*        <tr {...row.getRowProps()}>*/}
                    {/*            {row.cells.map(cell => {*/}
                    {/*                return (*/}
                    {/*                    <td {...cell.getCellProps()}>*/}
                    {/*                        {cell.render('Cell')}*/}
                    {/*                    </td>*/}
                    {/*                )*/}
                    {/*            })}*/}
                    {/*        </tr>*/}
                    {/*    )*/}
                    {/*})}*/}

                    {data.map((row, i) => {
                        return (
                            <tr {...row.getRowProps()}>

                                {row.cells.map(cell => {
                                    return (
                                    <td {...cell.getCellProps()}>
                                        {cell.render('Cell')}
                                    </td>
                                    )
                                })}

                                <td className="td-table-text">{row.col1}</td>
                                <td className="td-table-text">
                                    <button className="resume-table-button">
                                        <Download size={18} color={"var(--black"} className={"download-employer-applies"}/>
                                        {row.col2}
                                    </button>
                                </td>
                                <td className="td-table-text">{row.col3}</td>
                                <td className="td-table-text">{row.col4}</td>
                            </tr>
                        )
                    })}

                    </tbody>


                </table>
                <p>Selected Rows: {Object.keys(selectedRowIds).length}</p>
                <pre>
        <code>
          {JSON.stringify(
              {
                  selectedRowIds: selectedRowIds,
                  'selectedFlatRows[].original': selectedFlatRows.map(
                      d => d.original
                  ),
              },
              null,
              2
          )}
        </code>
      </pre>
            </div>
        )
    }

    // const tableInstance = useTable({ columns, data })

    return(
        <div className="container padding-top-bottom-cabinet">
            <div className="row center-row">
                <div className="employer-switch-container">
                    <EmployerSwitch/>
                </div>
            </div>

            <div className="row center-row">
                <div className="col-7 applies-col-display">
                    <div className="button-blue-div-employer-applies">
                        <button className="blue-link-button-applies">
                            <ChevronLeft size={20} color={"var(--blue"} className={"chevron-employer-applies"}/>
                            Назад до вакансій
                        </button>
                    </div>

                    <div className="employer-vacancies-applies-text">
                        <p className="title-of-page-applies">
                            Відгуки на вакансію
                        </p>
                        <p className="title-of-page-applies-vacancy">
                            Менеджер з комунікацій
                        </p>
                    </div>
                </div>
            </div>

            <div className="row center-row">
                <div className="col-8 applies-col-display">

                    <Table columns={columns} data={data} />

                    {/*<div className="table-div-container">*/}
                    {/*    <table className="table-container">*/}
                    {/*        <tr>*/}
                    {/*            <th className="title-table-text">Прізвище та ім'я</th>*/}
                    {/*            <th className="title-table-text">Резюме</th>*/}
                    {/*            <th className="title-table-text">Контакти</th>*/}
                    {/*            <th className="title-table-text">Дата подачі</th>*/}
                    {/*        </tr>*/}
                    {/*        <tr>*/}
                    {/*            <td className="td-table-text">Коваленко Анна</td>*/}
                    {/*            <td className="td-table-text">*/}
                    {/*                <button className="resume-table-button">*/}
                    {/*                    <Download size={18} color={"var(--black"} className={"download-employer-applies"}/>*/}
                    {/*                    Resume_1.pdf*/}
                    {/*                </button>*/}
                    {/*            </td>*/}
                    {/*            <td className="td-table-text">annakonovalenko@gmail.com</td>*/}
                    {/*            <td className="td-table-text">28.10</td>*/}
                    {/*        </tr>*/}
                    {/*    </table>*/}
                    {/*</div>*/}
                </div>
            </div>

        </div>

    )
}
