import React, {useState} from 'react';
import {ChevronRight} from "react-feather"
import EmployerSwitch from "../components/employerSwitch"
import TextEditor from "../components/textEditor"
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import UploadAvatar from '../components/employerLogoChoose'

function validate(employerName1, employerDescription1, position1) {
    const errors = {
        employerName1: '',
        employerDescription1: '',
        position1: '',
    };

    if (employerName1 === '') {
        errors['employerName1']="Name can't be empty";
    }

    if (employerDescription1 === '') {
        errors['employerDescription1']="Email should be at least 5 charcters long"
    }

    if (position1 === '') {
        errors['position']="Email should be at least 5 charcters long"
    }

    // console.log(errors)
    return errors;
}

export default function CreateVacancy() {

    const [showCity, setShowCity] = useState(false)

    const [state, setState] = useState({
        employerName: '',
        employerLogo: '',
        employerDescription: '',
        position: '',
        category: '',
        salary: '',
        location: '',
        vacancyDescription: '',
        errors: {
            employerName1: '',
            employerDescription1: '',
            position1: '',
        }
    })

    function handleChangeInput(event) {
        let errors = state.errors

        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        setState({[name]: value, errors});
    }

    function handleSubmit(event){
        event.preventDefault();

        const errors = validate(state.employerName, state.employerDescription, state.position);

        if (errors) {
            setState({ errors });
            return;
        }

    }

    return(
        <div className="container padding-top-bottom-cabinet">
            <div className="row center-row">
                <div className="employer-switch-container">
                    <EmployerSwitch/>
                </div>
            </div>
            <div className="row center-row">
                <div className="col-7">
                    <form>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва компанії
                            </label>
                            <input
                                name="employerName"
                                className="input-field"
                                type="text"
                                value={state.employerName}
                                onChange={handleChangeInput}
                            />
                            {
                                state.errors.employerName1 !== '' ? <span className='error-text'>{state.errors.employerName1}</span>
                                    : ''
                            }
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Логотип компанії
                            </label>
                            <div className="upload-picture">
                                {/*<UploadAvatar/>*/}
                            </div>
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Чим займається Ваша компанія?
                            </label>
                            <textarea
                                name="employerDescription"
                                rows="4"
                                className="input-field height-unset"
                                value={state.employerDescription}
                                onChange={handleChangeInput}
                            ></textarea>
                            {
                                state.errors.employerDescription1 !== '' ? <span className='error-text'>{state.errors.employerDescription1}</span>
                                    : ''
                            }
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва позиції
                            </label>
                            <input
                                className="input-field"
                                type="text"
                                value={state.position}
                                onChange={handleChangeInput}
                            />
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Категорія
                            </label>
                            <input
                                className="input-field"
                                type="text"
                            />
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Зарплатня
                            </label>
                            <input
                                className="input-field"
                                type="text"
                                placeholder="Приклад: 10 000 або 10 000-15 000"
                                value={state.salary}
                                onChange={handleChangeInput}
                            />
                        </div>
                        <div className="form-block-create">
                            <label name="location" className="form-label">
                                Локація
                            </label>
                            {/*<div className="radio-group">*/}
                            {/*    <Radio />*/}
                            {/*    <label>Віддалено</label>*/}
                            {/*</div>*/}
                            <RadioGroup
                                aria-label="location"
                                // defaultValue="remote"
                                name="radio-buttons-group"
                            >
                                <div className="radio-button-div" onClick={()=>setShowCity(false)}>
                                    <FormControlLabel
                                        value="remote"
                                        control={<Radio />}
                                        label="Віддалено"
                                    />
                                </div>
                                <div className="radio-button-div" onClick={()=>setShowCity(true)}>
                                    <FormControlLabel
                                        value="office"
                                        control={<Radio />}
                                        label="В офісі"
                                    />
                                    {
                                        showCity ? <input className="input-field" type="text"/> : false
                                    }

                                </div>

                                </RadioGroup>
                            </div>

                        <div className="form-block-create">
                            <label className="form-label">
                                Опис вакансії: задачі, вимоги, умови
                            </label>
                            <TextEditor/>
                        </div>
                        <div className="button-div-create-vacancy" onClick={handleSubmit}>
                            <button className="black-button">
                                Надіслати на модерацію
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>

    )
}
