import React, {useState} from 'react';
import {ChevronRight} from "react-feather"
import EmployerSwitch from "../components/employerSwitch"
import TextEditor from "../components/textEditor"
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import UploadAvatar from '../components/employerLogoChoose'

export default function CreateVacancy() {

    const [showCity, setShowCity] = useState(false)

    const [state, setState] = useState({
        employerName: '',
        employerLogo: '',
        employerDescription: '',
        position: '',
        category: '',
        salary: '',
        location: '',
        vacancyDescription: '',
        errors: {
            fullName: '',
            email: '',
            password: '',
        }
    })

    const validateForm = (errors) => {
        let valid = true;
        Object.values(errors).forEach(
            (val) => val.length > 0 && (valid = false)
        );
        return valid;
    }

    const handleChangeInput = (event) => {
        event.persist();
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        let errors = this.state.errors;

        switch (name) {
            case 'fullName':
                errors.fullName =
                    value.length < 5
                        ? 'Full Name must be 5 characters long!'
                        : '';
                break;
            case 'email':
                errors.email =
                    validEmailRegex.test(value)
                        ? ''
                        : 'Email is not valid!';
                break;
            case 'password':
                errors.password =
                    value.length < 8
                        ? 'Password must be 8 characters long!'
                        : '';
                break;
            default:
                break;
        }

        setState({errors, [name]: value});
    }

    handleSubmit = (event) => {
        event.preventDefault();
        if(validateForm(this.state.errors)) {
            console.info('Valid Form')
        }else{
            console.error('Invalid Form')
        }
    }

    return(
        <div className="container padding-top-bottom-cabinet">
            <div className="row center-row">
                <div className="col-8">
                    <EmployerSwitch/>
                </div>
            </div>
            <div className="row center-row">
                <div className="col-7">
                    <form onSubmit={handleSubmit}>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва компанії
                            </label>
                            <input
                                className="input-field"
                                type="text"
                                value={state.employerName}
                                onChange={handleChangeInput}
                            />
                            {
                                errors.fullName.length > 0 && <span className='error'>{errors.fullName}</span>
                            }
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Логотип компанії
                            </label>
                            <div className="upload-picture">
                                {/*<UploadAvatar/>*/}
                            </div>
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Чим займається Ваша компанія?
                            </label>
                            <textarea
                                rows="4"
                                className="input-field height-unset"
                                value={state.employerDescription}
                                onChange={handleChangeInput}
                            ></textarea>
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва позиції
                            </label>
                            <input
                                className="input-field"
                                type="text"
                                value={state.position}
                                onChange={handleChangeInput}
                            />
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Категорія
                            </label>
                            <input
                                className="input-field"
                                type="text"
                            />
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Зарплатня
                            </label>
                            <input
                                className="input-field"
                                type="text"
                                placeholder="Приклад: 10 000 або 10 000-15 000"
                                value={state.salary}
                                onChange={handleChangeInput}
                            />
                        </div>
                        <div className="form-block-create">
                            <label name="location" className="form-label">
                                Локація
                            </label>
                            {/*<div className="radio-group">*/}
                            {/*    <Radio />*/}
                            {/*    <label>Віддалено</label>*/}
                            {/*</div>*/}
                            <RadioGroup
                                aria-label="location"
                                // defaultValue="remote"
                                name="radio-buttons-group"
                            >
                                <div className="radio-button-div" onClick={()=>setShowCity(false)}>
                                    <FormControlLabel
                                        value="remote"
                                        control={<Radio />}
                                        label="Віддалено"
                                    />
                                </div>
                                <div className="radio-button-div" onClick={()=>setShowCity(true)}>
                                    <FormControlLabel
                                        value="office"
                                        control={<Radio />}
                                        label="В офісі"
                                        onChange={console.log("hello")}
                                    />
                                    {
                                        showCity ? <input className="input-field" type="text"/> : false
                                    }

                                </div>

                                </RadioGroup>
                            </div>

                        <div className="form-block-create">
                            <label className="form-label">
                                Опис вакансії: задачі, вимоги, умови
                            </label>
                            <TextEditor/>
                        </div>
                        <div className="button-div-create-vacancy">
                            <button className="black-button">
                                Надіслати на модерацію
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>

    )
}
