import React, {Component, useState} from "react";
import { default as ReactSelect } from "react-select";
import { components } from "react-select";

const Option = (props) => {
    return (
        <div>
            <components.Option {...props}>
                <input
                    className="checkbox"
                    type="checkbox"
                    checked={props.isSelected}
                    onChange={() => null}
                />
                <label className="label-form">{props.value}</label>
            </components.Option>
        </div>
    );
};

export default function SelectForm(props) {

    const [optionSelected, setOptionSelected] = useState(null)

    const colourOptions = [
        { value: "ocean", label: "Дизайн" },
        { value: "blue", label: "Аналітика" },
        { value: "purple", label: "Програмування" },
        { value: "red", label: "Дизайн" },
        { value: "orange", label: "Дизайн" },
        { value: "yellow", label: "Дизайн" },
        { value: "green", label: "Дизайн" },
        { value: "forest", label: "Дизайн" },
    ];

    return (
        <React.Fragment>
            <label className="label-form">{props.title}</label>
            <span
                class="d-inline-block"
                data-toggle="popover"
                data-trigger="focus"
            >
                <ReactSelect
                    placeholder={"Оберіть категорію"}
                    options={colourOptions}
                    isMulti
                    closeMenuOnSelect={false}
                    hideSelectedOptions={false}
                    components={{
                        Option
                    }}
                    onChange={(selected) => setOptionSelected(selected)}
                    allowSelectAll={false}
                    value={optionSelected}
                    className={"select-form"}
                />
            </span>
        </React.Fragment>
    );

}
