import React, {useEffect, useRef, useState} from 'react';
import {ChevronRight} from "react-feather"
import EmployerSwitch from "./employerSwitch"
import TextEditor from "./textEditor"
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import UploadAvatar from './employerLogoChoose'
import SimpleReactValidator from 'simple-react-validator';

export default function CreateVacancy() {

    const [showCity, setShowCity] = useState(false)
    const validator = useRef(new SimpleReactValidator());
    const [, forceUpdate] = useState();

    const initialValues = {
        employerName: '',
        // employerLogo: '',
        employerDescription: '',
        position: '',
    };
    const [formValues, setFormValues] = useState(initialValues);

    // const [state, setState] = useState({
    //     employerName: '',
    //     // employerLogo: '',
    //     employerDescription: '',
    //     position: '',
    //     // category: '',
    //     // salary: '',
    //     // location: {
    //     //     city: ''
    //     // },
    //     // vacancyDescription: '',
    // })
    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);

    function handleChangeInput(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        setState({[name]: value});
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);
    }

    useEffect(() => {
        if(Object.keys(formErrors).length === 0 && isSubmit){
            console.log(formValues);
        }
    }, [formErrors]);

    const validate = (values) =>{
        const errors = {};
        const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
        if(!values.employerName){
            errors.employerName = "username required";
        }
        if(!values.employerDescription){
            errors.employerDescription = "email required";
        }
        if(!values.position){
            errors.position = "password required";
        }
        return errors;
    }

    // function handleSubmit(event){
    //     event.preventDefault();
    //     if (validator.current.allValid()) {
    //         console.log("success");
    //     } else {
    //         validator.current.showMessages();
    //         forceUpdate(1);
    //         console.log("not success");
    //     }
    //
    // }

    return(
        <div className="container padding-top-bottom-cabinet">
            <div className="row center-row">
                <div className="employer-switch-container">
                    <EmployerSwitch/>
                </div>
            </div>
            <div className="row center-row">
                <div className="col-12 col-md-10 col-lg-8">
                    <form onSubmit={handleSubmit}>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва компанії
                            </label>
                            <input
                                className={
                                    (formErrors.employerName && isSubmit) ?
                                        "input-field height-unset error-border" : "input-field height-unset"
                                }
                                name="employerName"
                                type="text"
                                value={formValues.employerName}
                                onChange={handleChangeInput}
                            />
                            {/*{validator.current.message('employerName', state.employerName, 'required|employerName', {messages: {required: 'Заповніть назву компанії'}, className: 'error-text'})}*/}
                        </div>
                        <p>{formErrors.employerName}</p>
                        {/*<div className="form-block-create">*/}
                        {/*    <label className="form-label">*/}
                        {/*        Логотип компанії*/}
                        {/*    </label>*/}
                        {/*    <div className="upload-picture">*/}
                        {/*        /!*<UploadAvatar/>*!/*/}
                        {/*    </div>*/}
                        {/*</div>*/}
                        <div className="form-block-create">
                            <label className="form-label">
                                Чим займається Ваша компанія?
                            </label>
                            <textarea
                                name="employerDescription"
                                rows="4"
                                className={
                                    (formErrors.employerName && isSubmit) ?
                                        "input-field height-unset error-border" : "input-field height-unset"
                                }
                                value={formValues.employerDescription}
                                onChange={handleChangeInput}
                            />
                            <p>{formErrors.employerName}</p>
                            {/*{validator.current.message('employerDescription', state.employerDescription, 'required|max:120|employerDescription',*/}
                            {/*    {*/}
                            {/*        messages: {*/}
                            {/*            required: 'Заповніть опис компанії',*/}
                            {/*            max: 'Опис компанії не має перевищувати 120 символів'*/}
                            {/*        },*/}
                            {/*        className: 'error-text'*/}
                            {/*    }*/}
                            {/*)}*/}
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва позиції
                            </label>
                            <input
                                name="position"
                                className={
                                    (formErrors.position && isSubmit) ?
                                        "input-field height-unset error-border" : "input-field height-unset"
                                }
                                type="text"
                                value={formValues.position}
                                onChange={handleChangeInput}
                            />
                            <p>{formErrors.position}</p>
                            {/*{validator.current.message('position', state.employerDescription, 'required|position',*/}
                            {/*    {*/}
                            {/*        messages: {*/}
                            {/*            required: 'Заповніть назву позиції',*/}
                            {/*        },*/}
                            {/*        className: 'error-text'*/}
                            {/*    }*/}
                            {/*)}*/}
                        </div>
                        {/*<div className="form-block-create">*/}
                        {/*    <label className="form-label">*/}
                        {/*        Категорія*/}
                        {/*    </label>*/}
                        {/*    <input*/}
                        {/*        className="input-field"*/}
                        {/*        type="text"*/}
                        {/*    />*/}
                        {/*</div>*/}
                        {/*<div className="form-block-create">*/}
                        {/*    <label className="form-label">*/}
                        {/*        Зарплатня*/}
                        {/*    </label>*/}
                        {/*    <input*/}
                        {/*        className="input-field"*/}
                        {/*        type="text"*/}
                        {/*        placeholder="Приклад: 10 000 або 10 000-15 000"*/}
                        {/*        value={state.salary}*/}
                        {/*        onChange={handleChangeInput}*/}
                        {/*    />*/}
                        {/*</div>*/}
                        {/*<div className="form-block-create">*/}
                        {/*    <label name="location" className="form-label">*/}
                        {/*        Локація*/}
                        {/*    </label>*/}
                        {/*    <RadioGroup*/}
                        {/*        aria-label="location"*/}
                        {/*        name="location"*/}
                        {/*        value={state.location}*/}
                        {/*    >*/}
                        {/*        <div className="radio-button-div" onClick={()=>setShowCity(false)}>*/}
                        {/*            <FormControlLabel*/}
                        {/*                value="remote"*/}
                        {/*                control={<Radio />}*/}
                        {/*                label="Віддалено"*/}
                        {/*            />*/}
                        {/*        </div>*/}
                        {/*        <div className="radio-button-div" onClick={()=>setShowCity(true)}>*/}
                        {/*            <FormControlLabel*/}
                        {/*                value="office"*/}
                        {/*                control={<Radio />}*/}
                        {/*                label="В офісі"*/}
                        {/*            />*/}
                        {/*            {*/}
                        {/*                showCity &&*/}
                        {/*                    <React.Fragment>*/}
                        {/*                        <input*/}
                        {/*                            name="locationCity"*/}
                        {/*                            className="input-field"*/}
                        {/*                            type="text"*/}
                        {/*                            value={state.location.city}*/}
                        {/*                            onChange={handleChangeInput}*/}
                        {/*                        />*/}
                        {/*                        {validator.current.message('locationCity', state.location.city, 'required|locationCity',*/}
                        {/*                            {*/}
                        {/*                                messages: {*/}
                        {/*                                    required: 'Вкажіть в якому місті розташований офіс компанії',*/}
                        {/*                                },*/}
                        {/*                                className: 'error-text'*/}
                        {/*                            }*/}
                        {/*                        )}*/}
                        {/*                    </React.Fragment>*/}
                        {/*            }*/}
                        {/*        </div>*/}
                        {/*    </RadioGroup>*/}

                        {/*    {validator.current.message('location', state.location, 'required|location',*/}
                        {/*        {*/}
                        {/*            messages: {*/}
                        {/*                required: 'Оберіть локацію, де працюватиме кандидат',*/}
                        {/*            },*/}
                        {/*            className: 'error-text'*/}
                        {/*        }*/}
                        {/*    )}*/}

                        {/*</div>*/}

                        {/*<div className="form-block-create">*/}
                        {/*    <label className="form-label">*/}
                        {/*        Опис вакансії: задачі, вимоги, умови*/}
                        {/*    </label>*/}
                        {/*    <TextEditor/>*/}
                        {/*</div>*/}
                        <div className="button-div-create-vacancy" onClick={handleSubmit}>
                            <button className="black-button">
                                Надіслати на модерацію
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>

    )
}
