import React, {useRef, useState} from 'react';
import {ChevronRight} from "react-feather"
import EmployerSwitch from "./employerSwitch"
import TextEditor from "./textEditor"
import Radio from '@mui/material/Radio';
import RadioGroup from '@mui/material/RadioGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import FormControl from '@mui/material/FormControl';
import FormLabel from '@mui/material/FormLabel';
import UploadAvatar from './employerLogoChoose'
import SimpleReactValidator from 'simple-react-validator';

export default function CreateVacancy() {

    const [showCity, setShowCity] = useState(false)
    const validator = useRef(new SimpleReactValidator());
    const [, forceUpdate] = useState();

    const [state, setState] = useState({
        employerName: null,
        employerLogo: '',
        employerDescription: '',
        position: '',
        category: '',
        salary: '',
        location: {
            city: ''
        },
        vacancyDescription: '',
        valid: false,
    })

    function handleChangeInput(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        setState({[name]: value});
    }

    function handleSubmit(event){
        event.preventDefault();
        if (validator.current.allValid()) {
            setState({valid: true})
            console.log("success");
        } else {
            validator.current.showMessages();
            setState({valid: false})
            forceUpdate(1);
        }

    }

    let employerNameValidator = validator.current.message('employerName', state.employerName, 'required|employerName', {messages: {required: 'Заповніть назву компанії'}, className: 'error-text'})

    return(
        <div className="container padding-top-bottom-cabinet">
            <div className="row center-row">
                <div className="employer-switch-container">
                    <EmployerSwitch/>
                </div>
            </div>
            <div className="row center-row">
                <div className="col-12 col-md-10 col-lg-8">
                    <form>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва компанії
                            </label>
                            <input
                                name="employerName"
                                className={
                                    employerNameValidator ? "input-field height-unset error-border" : "input-field height-unset"
                                }
                                type="text"
                                value={state.employerName}
                                onChange={handleChangeInput}
                            />
                            {validator.current.message('employerName', state.employerName, 'required|employerName', {messages: {required: 'Заповніть назву компанії'}, className: 'error-text'})}
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Логотип компанії
                            </label>
                            <div className="upload-picture">
                                {/*<UploadAvatar/>*/}
                            </div>
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Чим займається Ваша компанія?
                            </label>
                            <textarea
                                name="employerDescription"
                                rows="4"
                                className={
                                    validator.current.fieldValid('employerDescription') ?
                                         "input-field height-unset" : "input-field height-unset error-border"
                                }
                                value={state.employerDescription}
                                onChange={handleChangeInput}
                            />
                            {validator.current.message('employerDescription', state.employerDescription, 'required|max:120|employerDescription',
                                {
                                    messages: {
                                        required: 'Заповніть опис компанії',
                                        max: 'Опис компанії не має перевищувати 120 символів'
                                    },
                                    className: 'error-text'
                                }
                            )}
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Назва позиції
                            </label>
                            <input
                                name="position"
                                className={
                                    validator.current.fieldValid('position') ?
                                        "input-field height-unset" : "input-field height-unset error-border"
                                }
                                type="text"
                                value={state.position}
                                onChange={handleChangeInput}
                            />
                            {validator.current.message('position', state.employerDescription, 'required|position',
                                {
                                    messages: {
                                        required: 'Заповніть назву позиції',
                                    },
                                    className: 'error-text'
                                }
                            )}
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Категорія
                            </label>
                            <input
                                className="input-field"
                                type="text"
                            />
                        </div>
                        <div className="form-block-create">
                            <label className="form-label">
                                Зарплатня
                            </label>
                            <input
                                className="input-field"
                                type="text"
                                placeholder="Приклад: 10 000 або 10 000-15 000"
                                value={state.salary}
                                onChange={handleChangeInput}
                            />
                        </div>
                        <div className="form-block-create">
                            <label name="location" className="form-label">
                                Локація
                            </label>
                            <RadioGroup
                                aria-label="location"
                                name="location"
                                value={state.location}
                            >
                                <div className="radio-button-div" onClick={()=>setShowCity(false)}>
                                    <FormControlLabel
                                        value="remote"
                                        control={<Radio />}
                                        label="Віддалено"
                                    />
                                </div>
                                <div className="radio-button-div" onClick={()=>setShowCity(true)}>
                                    <FormControlLabel
                                        value="office"
                                        control={<Radio />}
                                        label="В офісі"
                                    />
                                    {
                                        showCity &&
                                            <React.Fragment>
                                                <input
                                                    name="locationCity"
                                                    className="input-field"
                                                    type="text"
                                                    value={state.location.city}
                                                    onChange={handleChangeInput}
                                                />
                                                {validator.current.message('locationCity', state.location.city, 'required|locationCity',
                                                    {
                                                        messages: {
                                                            required: 'Вкажіть в якому місті розташований офіс компанії',
                                                        },
                                                        className: 'error-text'
                                                    }
                                                )}
                                            </React.Fragment>
                                    }
                                </div>
                            </RadioGroup>

                            {validator.current.message('location', state.location, 'required|location',
                                {
                                    messages: {
                                        required: 'Оберіть локацію, де працюватиме кандидат',
                                    },
                                    className: 'error-text'
                                }
                            )}

                        </div>

                        <div className="form-block-create">
                            <label className="form-label">
                                Опис вакансії: задачі, вимоги, умови
                            </label>
                            <TextEditor/>
                        </div>
                        <div className="button-div-create-vacancy" onClick={handleSubmit}>
                            <button className="black-button">
                                Надіслати на модерацію
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>

    )
}
