import React, {Component, useState} from "react";
import useForm from '../formHooks';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListItemText from '@mui/material/ListItemText';
import Select from '@mui/material/Select';
import Checkbox from '@mui/material/Checkbox';
import {Sliders, ChevronDown} from "react-feather";
import SelectForm from "../selectForm";
import CheckboxGroup from "../checkboxGroup";

export default function Filters() {

    const [city, setCity] = useState(null)
    const [location, setLocation] = useState(null)
    const [category, setCategory] = useState(null)
    const [schedule, setSchedule] = useState(null)

    const cities = [
        {value: 'Київ', label: "Київ"},
        {value: 'Одеса', label: "Одеса"},
        {value: 'Львів', label: "Львів"},
    ]

    const categories = [
        {value: 'IT-розробка', label: "IT-розробка"},
        {value: 'Аналітика', label: "Аналітика"},
        {value: 'Дизайн', label: "Дизайн"},
        {value: 'Маркетинг', label: "Маркетинг"},
        {value: 'Фінанси', label: "Фінанси"},
        {value: 'Аудит', label: "Аудит"},
        {value: 'Право', label: "Право"},
        {value: 'Логістика', label: "Логістика"},
    ];

    const schedules = [
        "Full-time", "Part-time", "Гнучкий full-time"
    ]

    const locations = [
        "Тільки віддалено"
    ]

    function handleInputChange(event) {
        const target = event.target;
        const value = target.type === 'checkbox' ? target.checked : target.value;
        const name = target.name;

        setSchedule({
            [name]: value
        });

        console.log(schedule)
    }

    return (
        <form className="filter-padding">
            <p className="filters-title-text">
                <Sliders size={22} color={"var(--gray)"} className={"filter-icon-margin"}/>
                Фільтри
            </p>
            {/*<SelectForm*/}
            {/*    name={"city"}*/}
            {/*    multiple={false}*/}
            {/*    title={"Місто"}*/}
            {/*    placeholder={"Оберіть місто"}*/}
            {/*    options={cities}*/}
            {/*    value={city}*/}
            {/*    onChange={handleInputChange}*/}
            {/*/>*/}
            {/*<CheckboxGroup*/}
            {/*    name={"location"}*/}
            {/*    title={null}*/}
            {/*    options={locations}*/}
            {/*    checked={location}*/}
            {/*    onChange={handleInputChange}*/}
            {/*/>*/}
            {/*<SelectForm*/}
            {/*    name={"category"}*/}
            {/*    multiple={true}*/}
            {/*    title={"Категорія"}*/}
            {/*    placeholder={"Оберіть категорію"}*/}
            {/*    options={categories}*/}
            {/*    value={category}*/}
            {/*    onChange={handleInputChange}*/}
            {/*/>*/}
            <CheckboxGroup
                name={"schedule"}
                title={"Графік"}
                options={schedules}
                checked={schedule}
                onChange={handleInputChange}
            />
        </form>
    );
}
