import * as React from 'react';
import OutlinedInput from '@mui/material/OutlinedInput';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import ListItemText from '@mui/material/ListItemText';
import Select from '@mui/material/Select';
import Checkbox from '@mui/material/Checkbox';
import {Sliders, ChevronDown} from "react-feather";
import SelectForm from "../selectForm";
import CheckboxGroup from "../checkboxGroup";

export default function Filters() {

    const cities = [
        "Київ",
        "Одеса",
        "Львів",
        "Харків",
        "Полтава",
        "Вінниця"
    ]

    const categories = [
        {value: 'IT-розробка', label: "IT-розробка"},
        {value: 'Аналітика', label: "Аналітика"},
        'Дизайн',
        'Маркетинг',
        'Фінанси',
        'Аудит',
        'Право',
        'Логістика',
        'Продажі',
        'Офіс-менеджмент',
    ];

    return (
        <form className="filter-padding">
            <p className="filters-title-text">
                <Sliders size={22} color={"var(--gray)"} className={"filter-icon-margin"}/>
                Фільтри
            </p>
            <SelectForm
                multiple={false}
                title={"Місто"}
                placeholder={"Оберіть місто"}
                options={cities}
            />
            <CheckboxGroup label={"Тільки віддалено"}/>
            <SelectForm
                multiple={true}
                title={"Категорія"}
                placeholder={"Оберіть категорію"}
                options={categories}
            />
        </form>
    );
}
